package practice;

public class DemoSubString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="Welcome to India";
		
		
		String abc=name.substring(4);
		System.out.println("The substring is "+abc);
		
		String xyz=name.substring(3, 7);
		System.out.println("The substring is "+xyz);

	}

}
